  it('should create a hash of a small object', () => {
    const val = { a: 1, b: 2, c: 3, d: 4 };
    const key = hash(val);
    expect(key).toEqual(3306510671);
  });

  it('should create a hash of a complex object', () => {
    const val = {
      a: [1111, 2222, 3333, 4444, 5555],
      b: { aa: 11, bb: 22, cc: 33, dd: 44, ee: 55 },
      c: 'cccccccccc',
      d: 4444444444,
    };
    const key = hash(val);
    expect(key).toEqual(1692924625);
  });

  it('should create a hash of a Set', () => {
    const val = new Set(['a', 'b', 'c']);
    const key = hash(val);
    expect(key).toEqual(954130181);
  });

  it('should create a hash of a Map', () => {
    const val = new Set([
      ['a', 1],
      ['b', 2],
      ['c', 3],
    ]);
    const key = hash(val);
    expect(key).toEqual(2465213849);
  });
